def RunPairingsTest():
    def printblue(s):print("\033[94m"+s+"\033[0;0m")        
    print("-------------------------------------------------------------------------------")
    printblue("Pairings computation verifications on BLS12-381 :")
    print("-------------------------------------------------------------------------------")
    PairingsEngine=pr.BLS12PairingsEngine(par.bls12_381_params)
    a = PairingsEngine.pickRandomScalar()
    b = PairingsEngine.pickRandomScalar()
    P = PairingsEngine.pickRandomG1()
    Q = PairingsEngine.pickRandomG2()
    aP=a*P
    bQ=b*Q
    e1=PairingsEngine.paire(P,Q)
    e2=PairingsEngine.paire(aP,bQ)
    print("\x1b[36m"+"Random point from G1 (E(Fp)) = "+"\x1b[0m")
    print(P)
    print("\x1b[36m"+"Random point from G2 (E(Fp2)) = "+"\x1b[0m")
    print(Q)
    print("\x1b[36m"+"Random scalar 'a' from Fr = "+"\x1b[0m")
    print(a)
    print("\x1b[36m"+"Random scalar 'b' from Fr = "+"\x1b[0m")
    print(b)
    print("\x1b[36m"+"[a]P = "+"\x1b[0m")
    print(aP)
    print("\x1b[36m"+"[b]Q = "+"\x1b[0m")
    print(bQ)
    print("\x1b[36m"+"e(P,Q) = "+"\x1b[0m")
    print(e1)
    print("\x1b[36m"+"e(aP,bQ) = "+"\x1b[0m")
    print(e2)
